mapper.select:
        /*根据产品编号，生效时间查询干线信息*/
        select t1.*,t0.U_portPrice as priceCode, t3.U_priceUDO as priceType,t4.ds_code as channel  from dbo.[@PRICEROUTING] t0
        inner join (
        select * from dbo.V_DSCardTFDetail  where ISNULL(U_StartTime, #{effectTime}) <= #{effectTime}
        and  ISNULL(U_EndTime, #{effectTime}) >= #{effectTime} ) t1
        on t0.U_WareHouseFrom = t1.companyFrom and t0.U_WareHouseOfPort = t1.companyTo and t0.U_TransferPrice = t1.tfCode
        left join [@PRICELIST] t2 on t0.U_portPrice = t2.code
        left join [@pricetype] t3 on t2.U_priceType = t3.code
        left join [tb_ds_channel] t4 on t0.U_ProductCode = t4.yw_code
        where ISNULL(t0.U_StartDate, #{effectTime}) <= #{effectTime}
        and  ISNULL(t0.U_EndDate, #{effectTime}) >= #{effectTime}
        and t0.U_ProductCode = #{productCode}
		order by t1.companyFrom asc
